import * as React from "react";
import * as classNames from "classnames";

import BoardSquare from "./BoardSquare";
import BoardPiece from "./BoardPiece";
import api from "./api";
import { PieceOnBoard } from "./GameRoom";
import "./Board.css";

interface State {
  activeIndex?: number;
  isLoading: boolean;
}

interface Props {
  pieces: PieceOnBoard[];
  roomId: string;
  userId?: string;
  isYourTurn: boolean;
  isPlaying: boolean;
}

const playSound = (() => {
  const sound = new Audio(
    "data:audio/wav;base64,"
  );
  return () => {
    sound.play();
  };
})();

class Board extends React.Component<Props, State> {
  public state: State = { isLoading: false };

  public componentDidUpdate(prevProps: Props) {
    const hasPiecesChanged =
      this.props.pieces &&
      !this.props.pieces.every(
        (piece, index) =>
          !!prevProps.pieces && prevProps.pieces[index] === piece
      );
    if (prevProps.pieces && hasPiecesChanged && this.props.isYourTurn) {
      playSound();
    }
  }

  public isDark = (index: number) => {
    const isEvenRow = Math.floor(index / 8) % 2 === 0;
    const isEvenFile = index % 2 === 0;
    return isEvenRow ? !isEvenFile : isEvenFile;
  };

  public calculateRank = (index: number) => {
    if (index < 8) {
      return 8;
    } else if (index < 16) {
      return 7;
    } else if (index < 24) {
      return 6;
    } else if (index < 32) {
      return 5;
    } else if (index < 40) {
      return 4;
    } else if (index < 48) {
      return 3;
    } else if (index < 56) {
      return 2;
    } else {
      return 1;
    }
  };

  public calculateFile = (index: number) => {
    if (index % 8 === 0) {
      return "a";
    } else if ((index - 1) % 8 === 0) {
      return "b";
    } else if ((index - 2) % 8 === 0) {
      return "c";
    } else if ((index - 3) % 8 === 0) {
      return "d";
    } else if ((index - 4) % 8 === 0) {
      return "e";
    } else if ((index - 5) % 8 === 0) {
      return "f";
    } else if ((index - 6) % 8 === 0) {
      return "g";
    } else {
      return "h";
    }
  };

  public selectSquare = (index: number) => {
    if (!this.props.isPlaying) {
      return;
    }

    if (this.state.activeIndex === index) {
      this.setState({ activeIndex: undefined });
    } else if (
      typeof this.state.activeIndex === "number" &&
      this.props.userId
    ) {
      this.setState({ isLoading: true });
      api
        .movePiece(
          this.props.roomId,
          this.props.userId,
          {
            file: this.calculateFile(this.state.activeIndex),
            rank: this.calculateRank(this.state.activeIndex)
          },
          {
            file: this.calculateFile(index),
            rank: this.calculateRank(index)
          }
        )
        .then(() => {
          this.setState({ activeIndex: undefined, isLoading: false });
        })
        .catch(error => {
          this.setState({ activeIndex: undefined, isLoading: false });
          alert(error.response.data.error);
        });
    } else {
      this.setState({ activeIndex: index });
    }
  };

  public render() {
    return (
      <div
        className={classNames("board", {
          "board--loading": this.state.isLoading,
          "board--playing": this.props.isPlaying
        })}
      >
        {Array.from({ length: 64 })
          .map((_, i) => i)
          .map(index => (
            <BoardSquare
              key={index}
              index={index}
              onPress={this.selectSquare}
              isDark={this.isDark(index)}
              isActive={this.state.activeIndex === index}
              isPlaying={this.props.isPlaying}
            />
          ))}
        {this.props.pieces.map((chessPiece, index) => (
          <BoardPiece key={index} chessPiece={chessPiece} />
        ))}
      </div>
    );
  }
}

export default Board;
